const AnimatedDock = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [isClicked, setIsClicked] = useState(null);

  return (
    <div className="flex justify-center items-end h-24 w-full p-2">
      <div className="relative flex items-end h-full glass-effect rounded-2xl p-2 shadow-lg">
        {/* Dock Icons */}
        {appIcons.map((app, index) => (
          <div
            key={app.id}
            className={`relative mx-1 transition-all duration-300 ease-in-out`}
            onMouseEnter={() => setHoveredIndex(index)}
            onMouseLeave={() => setHoveredIndex(null)}
            onClick={() => handleClick(index)}
          >
            {/* Icon with hover effects */}
            <div className="w-12 h-12 rounded-xl flex items-center justify-center overflow-hidden">
              <img src={app.logo} alt={app.name} className="w-8 h-8 object-contain" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Animation and State Management
const handleClick = (index) => {
  setIsClicked(index);
  setTimeout(() => setIsClicked(null), 600);
};

// Scale calculation based on hover position
const calculateScale = (index) => {
  if (hoveredIndex === null) return 1;
  const distance = Math.abs(hoveredIndex - index);
  if (distance === 0) return 1.5;
  if (distance === 1) return 1.25;
  if (distance === 2) return 1.1;
  return 1;
};

// Glass effect styling
const glassEffect = {
  background: 'rgba(255, 255, 255, 0.1)',
  backdropFilter: 'blur(10px)',
  border: '1px solid rgba(255, 255, 255, 0.2)'
};
